import { __decorate } from "tslib";
import { Injectable } from '@angular/core';
import { HttpService } from '@core-service/http.service';
import { environment } from 'src/environments/environment';
import { HttpClient, HttpHeaders } from '@angular/common/http';
let VacunaService = class VacunaService {
    constructor(http, httpa) {
        this.http = http;
        this.httpa = httpa;
    }
    consultarVacunasAplicadas(estado, idUsuario) {
        console.log(idUsuario);
        return this.httpa.get(`${environment.endpoint}${estado}/1`);
    }
    consultarVacunasPendeintes(estado, idUsuario) {
        console.log(idUsuario);
        console.log(`${environment.endpoint}${estado}/1`);
        return this.httpa.get(`${environment.endpoint}${estado}/1`);
    }
    /**
     * Registra una vacuna
     * @param vacuna
     * @returns
     */
    registrarVacuna(vacuna) {
        if (vacuna.dosisPendiente) {
            vacuna.dosisPendiente = 'S';
        }
        else {
            vacuna.dosisPendiente = 'N';
        }
        if (vacuna.subsidiada) {
            vacuna.subsidiada = 'S';
        }
        else {
            vacuna.subsidiada = 'N';
        }
        console.log(vacuna);
        console.log(environment.endpoint);
        this.headers = new HttpHeaders({ 'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Headers': 'X-API-KEY, Origin, X-Requested-With, Content-Type, Accept, Access-Control-Request-Method',
            'Access-Control-Allow-Methods': 'GET, POST, OPTIONS, PUT, DELETE',
            'Allow': 'GET, POST, OPTIONS, PUT, DELETE' });
        return this.httpa.post(`${environment.endpoint}`, vacuna, { headers: this.headers, responseType: 'json' });
    }
    /**
     * Actualiza el estado de la vacuna
     * @param vacuna
     * @returns
     */
    aplicarVacuna(vacuna) {
        return this.httpa.put(`${environment.endpoint}${vacuna.id}`, vacuna);
    }
    guardarUsuario(usuario) {
        console.log(`${environment.endpointusuario}`);
        return this.httpa.post(`${environment.endpointusuario}`, usuario);
    }
};
VacunaService.ctorParameters = () => [
    { type: HttpService },
    { type: HttpClient }
];
VacunaService = __decorate([
    Injectable()
], VacunaService);
export { VacunaService };
