import { __decorate } from "tslib";
import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment';
import { HttpClient } from '@angular/common/http';
import { HttpService } from '@core/services/http.service';
let VacunaService = class VacunaService {
    constructor(httpa, http) {
        this.httpa = httpa;
        this.http = http;
    }
    /**
     * Consulta vacunas en estado aplicada
     * @param tipoDoc
     * @param documento
     * @param estado
     * @returns
     */
    consultarVacunasAplicadas(tipoDoc, documento, estado) {
        return this.httpa.get(`${environment.endpoint}vacunas/${estado}/${tipoDoc}/${documento}`);
    }
    /**
     * Consulta vacunas en estado pendiente
     * @param tipoDoc
     * @param documento
     * @param estado
     * @returns
     */
    consultarVacunasPendeintes(tipoDoc, documento, estado) {
        return this.httpa.get(`${environment.endpoint}vacunas/${estado}/${tipoDoc}/${documento}`);
    }
    /**
     * Registra una vacuna
     * @param vacuna
     * @returns
     */
    registrarVacuna(vacuna) {
        if (vacuna.dosisPendiente) {
            vacuna.dosisPendiente = 'S';
        }
        else {
            vacuna.dosisPendiente = 'N';
        }
        if (vacuna.subsidiada) {
            vacuna.subsidiada = 'S';
        }
        else {
            vacuna.subsidiada = 'N';
        }
        return this.http.doPost(`${environment.endpoint}vacunas`, vacuna, this.http.optsName('crear vacunas'));
    }
    /**
     * Actualiza el estado de la vacuna
     * @param vacuna
     * @returns
     */
    aplicarVacuna(vacuna) {
        return this.httpa.put(`${environment.endpoint}vacunas/${vacuna.id}`, vacuna);
    }
    /**
     * Registra un usuario
     * @param usuario
     * @returns
     */
    guardarUsuario(usuario) {
        console.log(`${environment.endpoint}usuarios/`);
        return this.httpa.post(`${environment.endpoint}usuarios/`, usuario);
    }
    /**
     * Consulta un usuario
     * @param tipoDoc
     * @param documento
     * @returns
     */
    consultarUsuario(tipoDoc, documento) {
        return this.httpa.get(`${environment.endpoint}usuarios/${tipoDoc}/${documento}`);
    }
};
VacunaService.ctorParameters = () => [
    { type: HttpClient },
    { type: HttpService }
];
VacunaService = __decorate([
    Injectable()
], VacunaService);
export { VacunaService };
